.about__aboutParent {
  height: 100vh;
  max-height: 100vh;
  max-width: 100vw;
  overflow: hidden;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 0px 15px;

  @media (max-height: 700px) {
    margin-top: 30px;
  }

  @media (max-height: 570px) {
    margin-top: 40px;
  }

  @media (max-height: 720px) and (max-width: 600px) {
    margin-top: 50px;
  }

  .about__background {
    position: absolute;
    object-fit: cover;
    width: 120vw;
    margin-left: -10vw;
    height: 120vh;
    margin-top: -10vh;
    z-index: 1;
    filter: brightness(0.6);

    @media (max-height: 720px) and (max-width: 600px) {
      width: 120vw;
      margin-left: -10vw;
      height: 120vh;
      margin-top: -10vh;
    }
  }

  .about__content {
    position: absolute;

    z-index: 15;

    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    row-gap: 50px;

    @media (max-width: 800px) and (max-height: 400px) {
      row-gap: 20px;
    }

    h1 {
      color: white;
      font-weight: bold;
      font-size: 56px;
      line-height: 66px;
      text-align: center;
      padding: 0px;
      margin: 0px;

      @media (max-height: 850px) {
        font-size: 40px;
        line-height: 20px;
      }

      @media (max-height: 550px) {
        font-size: 30px;
        line-height: 0px;
      }
    }

    .about__logo {
      width: 50vw;
      max-width: 300px;

      @media (max-height: 850px) {
        width: fit-content;
        height: 20vh;
      }
    }

    .about__text {
      color: white;
      max-width: 700px;
      font-family: Raleway, sans-serif;
      font-style: normal;
      font-weight: normal;
      font-size: 24px;
      line-height: 28px;
      text-align: center;
      padding: 0px 15px;

      @media (max-height: 620px) {
        font-size: 18px;
      }

      @media (max-height: 720px) and (max-width: 600px) {
        font-size: 16px;
        padding: 5px 10px;
      }
    }

    .about__logoAndName {
      display: flex;
      flex-direction: column;
      align-items: center;
      justify-content: center;
      row-gap: 15px;
      color: white;

      & h1:nth-child(n) {
        font-family: "Aldrich", sans-serif;
        font-style: normal;
        font-weight: normal;
        font-size: 56px;
        line-height: 55px;
        text-align: center;

        @media (max-height: 850px) {
          font-size: 40px;
        }

        @media (max-height: 550px) {
          font-size: 30px;
          line-height: 20px;
        }

        @media (max-width: 300px) and (max-height: 660px) {
          font-size: 25px;
          line-height: 0px;
          margin-top: 15px;
          margin-bottom: -10px;
        }
      }
    }
  }

  .about__backgroundParallax {
    position: absolute;
    top: 0px;
    left: 0px;
    z-index: 2;
    width: 100%;
    overflow: hidden;
    height: 100vh;

    & img:nth-child(1) {
      display: block;
      width: 40vw;
      max-width: 800px;
      position: absolute;
      top: 10vw !important;
      right: auto;
      left: 0px !important;
      z-index: 5;

      @media (max-width: 2200px) {
        left: -15% !important;
      }

      @media (max-width: 1500px) {
        left: -10% !important;
        top: 20vw !important;
      }

      @media (max-width: 1000px) {
        display: none !important;
      }
    }

    & img:nth-child(2) {
      z-index: 5;
      display: block;
      width: 40vw;
      max-width: 900px;
      position: absolute;
      top: 00px;
      left: auto !important;
      right: 0px !important;

      @media (max-width: 2200px) {
        right: -15% !important;
      }

      @media (max-width: 1500px) {
        right: -20% !important;
        top: 15vw !important;
      }

      @media (max-width: 1000px) {
        display: none !important;
      }
    }
  }
}
